mapper.update:
UPDATE member
SET shipping_methods = #{vos, typeHandler=com.campgem.common.typehandler.JsonListTypeHandler}
WHERE user_base_id = #{uid}
  AND del_flag = 0

mapper.select:
SELECT shipping_methods
FROM member
WHERE user_base_id = #{uid}

SELECT *
FROM member m
WHERE m.user_base_id = #{id}
